aKa={version:"0.1",versionDate:"July 24, 2012",validateForm:function(b){var a=aKa.Validation.validateFormFields(b);console.log("Form passes validation? "+a[0]);if(a[1]!==false){alert(a[1])}return a[0]}};aKa.Validation={verbose:true,cls:".aka-validation",msgJoin:"\n",emailRegEx:/^(\w+)([\-+.][\w]+)*@(\w[\-\w]*\.){1,5}([A-Za-z]){2,6}$/,emailErrMsg:"Please enter a valid email address.",phoneRegEx:/^([0-9]{3})([-,\.,\s]?)([0-9]{3})([-,\.,\s]?)([0-9]{4})$/,phoneErrMsg:"Please enter a valid US phone number in 123-456-7890 format.",textRegEx:/^.{5,}$/,textErrMsg:"Please enter valid text.",testEmail:function(a){return this.emailRegEx.test(a)},testPhone:function(a){return this.phoneRegEx.test(a)},testText:function(a){return this.textRegEx.test(a)},validateFormFields:function(h){var d=$(h),g=d.find(this.cls),b=0,c=0,f=[],e,a;for(c;c<g.length;c++){e=$(g[c]);if(e.attr("vtype")==="text"){if(this.testText(e[0].value)){b++}else{if(!a){a=e}f.push(this.textErrMsg)}}else{if(e.attr("vtype")==="email"){if(this.testEmail(e[0].value)){b++}else{if(!a){a=e}f.push(this.emailErrMsg)}}else{if(e.attr("vtype")==="phone"){if(this.testPhone(e[0].value)){b++}else{if(!a){a=e}f.push(this.phoneErrMsg)}}else{b++}}}}if(a){a.focus()}return[(b===g.length),(f.length>0&&this.verbose?f.join(this.msgJoin):false)]}};
